const defaultBoard = [];
for(let i = 0; i < 10; i++) {
    defaultBoard.push([])
    for(let j = 0; j < 10; j++){
        defaultBoard[i].push("hi")
    }
}

export default function boardReducer (
    state = defaultBoard, action
){
    if(action.type == "boardClick") {
        return;
    }
    return state;
}
