const defaultBoard = [];
for(let i = 0; i < 10; i++) {
    defaultBoard.push([])
    for(let j = 0; j < 10; j++){
        if (j % 2 === 0){
            defaultBoard[i].push("yes");
        }
        else {
            defaultBoard[i].push("no")
        }
    }
}

export default function boardReducer (
    state = defaultBoard, action
){
    if(action.type === "boardClick") {
        if (state[action.need[0]][action.need[1]] === "yes" || state[action.need[0]][action.need[1]] === "no") {
            state[action.need] = "deactive"
        }if (state[action.need[0]][action.need[1]] === "deactive") {
            return state;;
        }
        return [...state];
    }
    return state;
}
