        if (x-i < 0 || x-i > 9 || defaultBoard[x-i][y] === "yes"){
            return false;
        }
    }
    return true;
}
//check up
function checkUp(shipLen,x,y) {
    for (let i = 1; i < shipLen+1; i++){
        if (y+i < 0 || y+i > 9 || defaultBoard[x][y+i] === "yes"){
            return false;
        }
    }
    return true;
}
//check down
function checkDown(shipLen,x,y) {
    for (let i = 1; i < shipLen+1; i++){
        if (y-i < 0 || y-i > 9 || defaultBoard[x][y-i] === "yes"){
            return false;
        }
    }
    return true;
}

//5*1
