const defaultBoard = [];
for(let i = 0; i < 10; i++) {
    defaultBoard.push([])
    for(let j = 0; j < 10; j++){
        if (j % 2 === 0){
            defaultBoard[i].push("yes");
        }
        else {
            defaultBoard[i].push("no")
        }
    }
}
const defaultState = {
    symbol: "",
    board:defaultBoard
}

function generateGameBoard() {
    return defaultState.board;
}
function generateSymbol() {
    return defaultState.symbol;
}

export default function boardReducer (state, action){
    if (state === undefined) {
        return generateGameBoard();
    }
    if(action.type === "boardClick") {
        const value = state[action.need[0]][action.need[1]];
        if (value === "yes") {
            state[action.need[0]][action.need[1]] = "Y";
        }if (value === "no") {
            state[action.need[0]][action.need[1]] = "Y";
        }
        return [...state];
    }
    return state;
}
