const defaultSymbol = "";
export default function symbolReducer (
    state = defaultSymbol, action
){
    if(action.type === "checkSymbol") {
        if (state = "" && action.status === "yes") {
            state = "X"
        } else if(state = "" && action.status === "no") {
            state = "Y"
        }else {
            return state;
        }
    }
    return state;
}
